const SRS_TERRAFORM_TOKEN = 'SRS_TERRAFORM_TOKEN';

const utils = require('js-core/utils');

export const Token = {
  save(data) {
    localStorage.setItem(SRS_TERRAFORM_TOKEN, JSON.stringify(data));
  },
  load() {
    const info = localStorage.getItem(SRS_TERRAFORM_TOKEN);
    if (!info) return null;

    const o = JSON.parse(info);
    return {token: o.token};
  },
  remove() {
    localStorage.removeItem(SRS_TERRAFORM_TOKEN);
  },
};

export const Tools = {
  mask(data) {
    const mask = `***${data.token.length}B***`;
    return JSON.stringify({...data, token: mask});
  }
};

export const Clipboard = {
  copy(text) {
    if (navigator.clipboard) {
      return navigator.clipboard.writeText(text);
    }

    const e = document.createElement("textarea");
    e.value = text;
    e.style.position = "fixed";

    document.body.appendChild(e);
    e.focus();
    e.select();

    return new Promise((resolve, reject) => {
      try {
        const success = document.execCommand('copy');
        if (success) {
          resolve(true);
        } else {
          reject(false);
        }
      } catch (e) {
        reject(e);
      } finally {
        document.body.removeChild(e);
      }
    });
  }
};

export const Errors = {
  auth: 2001, // Verify token failed.
};

export const StreamURL = {
  build: (vhost, app, stream) => {
    return utils.streamURL(vhost, app, stream);
  },
};

export const PlatformPublicKey = `ssh-rsa  video@MB0`;

export function RandomString(length, space) {
  var text = '';
  for (let i = 0; i < length; ++i) {
    text += space.charAt(Math.floor(Math.random() * space.length));
  }
  return text;
}
